        setButtonTooltips();
        enableActions();

        return;
    }

    if ("closable" == prop) {

        if ((Boolean) evt.getNewValue() == Boolean.TRUE) {
            add(closeButton);
        } else {
            remove(closeButton);
        }
    } else if ("maximizable" == prop) {

        if ((Boolean) evt.getNewValue() == Boolean.TRUE) {
            add(maxButton);
        } else {
            remove(maxButton);
        }
    } else if ("iconable" == prop) {

        if ((Boolean) evt.getNewValue() == Boolean.TRUE) {
            add(iconButton);
        } else {
            remove(iconButton);
        }
    }

    enableActions();

    revalidate();
    repaint();
}

